import { useEffect } from "react";

const Scroller = () => {
    useEffect(() => window.addEventListener("scroll", handleScroll));

    const handleScroll = () => {
        if (window.scrollY < 250) {
            console.log(window.scrollY);
        }
    };

    return <div>croller</div>;
};

export default Scroller;
