const Input = ({ type, placeholder, recievedStyle }) => {
    switch (type) {
        case "textarea":
            return (
                <input placeholder={placeholder} className={recievedStyle} />
            );
        case "select":
            return (
                <select className={recievedStyle}>
                    <option value="" disabled selected hidden>
                        {placeholder}
                    </option>
                </select>
            );
        default:
            return (
                <React.Fragment>
                    <input type="text" {...props} />
                </React.Fragment>
            );
    }
};

export default Input;
