import { useEffect, useState } from "react";
import svgStyle from "../common/Scroller.scss";
import icon from "../../img/_up_icon.svg";
import getReactWithCX from "./react-cx";

const Scroller = () => {
    const [scrollHit, setScrollHit] = useState(false);

    useEffect(() => window.addEventListener("scroll", handleScroll));

    const handleScroll = () => {
        if (!(window.scrollY < 250)) {
            setScrollHit(true);
        }
        setScrollHit(false);
    };

    return (
        <div className={svgStyle["container"]}>
            <img
                src={icon}
                className={cx(
                    svgStyle["icon"],
                    scrollHit ? svgStyle["icon__visible"] : none
                )}
            ></img>
        </div>
    );
};

export default Scroller;
